mapper.update:
update book
set book_state_id = #{bookStateId}
where book_id = #{bookId}

update book
set book_state_id = 10
where book_id = #{_parameter}

mapper.select:
select b.book_Id, b.user_id, b.book_Count, b.book_Insert_Date, b.book_Update_Date, b.book_Memo, b.book_Total_Fee, bs.book_state_id, bs.book_state_name, bm.book_method_id, bm.book_method_name, l.laundry_id, l.laundry_name, bl.book_line_id, bl.book_line_fee, bl.clothes_Id, bl.fabric_Id from book b join book_state bs on b.book_state_id = bs.book_state_id join book_method bm on bm.book_method_id = b.book_method_id join laundry l on l.laundry_id = b.laundry_id join book_line bl on b.book_id = bl.book_id where b.user_id = #{userId}
bs.book_state_id <= #{bookStateId}
order by book_insert_date desc

select b.book_Id, b.user_id, b.book_Count, b.book_Insert_Date, b.book_Update_Date, b.book_Memo, b.book_Total_Fee, bs.book_state_id, bs.book_state_name, bm.book_method_id, bm.book_method_name, l.laundry_id, l.laundry_name, bl.book_line_id, bl.book_line_fee, bl.clothes_Id, bl.fabric_Id from book b join book_state bs on b.book_state_id = bs.book_state_id join book_method bm on bm.book_method_id = b.book_method_id join laundry l on l.laundry_id = b.laundry_id join book_line bl on b.book_id = bl.book_id where b.laundry_id = #{laundryId}
bs.book_state_id between #{start} and #{end}
order by book_insert_date desc
